course_data = FileAttachment("courses.csv").csv({typed: true})
access_modes = FileAttachment("assets/access_modes.json").json()
audiences = FileAttachment("assets/audiences.json").json()
educational_levels = FileAttachment("assets/education_levels.json").json()
resource_types = FileAttachment("assets/resource_types.json").json()







































Code
search_query = (search_terms ?? "").toLowerCase().trim()









Code
viewof search_terms = Inputs.text({placeholder: "Type to search..."})









Advanced search…



Code
viewof selected_access_mode = Inputs.checkbox(access_modes, {label: "Access mode", value: []})
viewof selected_audience = Inputs.select(audiences, {label: "Audience", value: null})
viewof selected_educational_level = Inputs.checkbox(educational_levels, {label: "Level", value: []})
viewof selected_resource_type = Inputs.select(resource_types, {label: "Type", value: null})

































Code
course_filtered =
  (
    (!selected_audience) &&
    (selected_access_mode ?? []).length === 0 &&
    (selected_educational_level ?? []).length === 0 &&
    (selected_resource_type ?? []).length === 0 &&
    (!search_query)
  )
  ? course_data.filter(row =&gt; row.featured === "yes")
  : course_data.filter(row =&gt;
      // audience
      (!selected_audience || row.audience.includes(selected_audience)) &&

      // access mode
      ((selected_access_mode ?? []).length === 0 ||
        (selected_access_mode ?? []).includes(row.accessMode)) &&

      // educational level
      ((selected_educational_level ?? []).length === 0 ||
        (selected_educational_level ?? []).includes(row.educationalLevel)) &&

      // resource type
      ((selected_resource_type ?? []).length === 0 ||
        (selected_resource_type ?? []).includes(row.learningResourceType)) &&

      // search
      (!search_query ||
        row.headline.toLowerCase().includes(search_query))
    )









Code
html`${
  course_filtered.length === 0
  ? html`
    &lt;div class="course-data-empty"&gt;
      &lt;p&gt;No results&lt;/p&gt;
    &lt;/div&gt;
  `
  : course_filtered.map(row =&gt; 
  html`
    &lt;div class="${row.featured === "yes" ? "course-data featured" : "course-data"}"&gt;
      &lt;h3&gt;
          &lt;a href=${row.url}&gt;${row.headline}&lt;/a&gt;
      &lt;/h3&gt;
      &lt;p&gt;${row.description}&lt;/p&gt;
    &lt;/div&gt;
  `
  )
}`
